      items: ["Website & UI/UX design", "Web banners", "Thumbnails", "Online graphic creation"],
      bgColor: "bg-gray-50",
      accentColor: "border-pink-500"
    },
    {
      title: "Marketing & Print",
      icon: <Megaphone className="w-12 h-12 text-orange-500" />,
      items: ["Brochures/Flyers", "Ad creatives", "Social media assets", "Presentations", "Posters", "Menus", "Signage"],
      bgColor: "bg-blue-50",
      accentColor: "border-orange-500"
    },
    {
      title: "The Plus",
      icon: <PlusCircle className="w-12 h-12 text-purple-500" />,
      items: ["Packaging & merchandise", "Cap/T-shirt designing", "Book covers", "Labels"],
      bgColor: "bg-purple-50",
      accentColor: "border-purple-500"
    }
  ];

  return (
    <div className="animate-in slide-in-from-right duration-700">
      <section className="bg-gradient-main py-20 text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h1 className="text-5xl font-black mb-6">Our Services</h1>
          <p className="text-xl max-w-2xl mx-auto opacity-90">
            Comprehensive creative solutions across 4 specialized pillars.
          </p>
        </div>
      </section>

      {sections.map((section, idx) => (
        <section key={idx} className={`${section.bgColor} py-24`}>
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className={`flex flex-col ${idx % 2 === 0 ? 'lg:flex-row' : 'lg:flex-row-reverse'} items-center gap-16`}>
              <div className="flex-1">
                <div className="mb-6">{section.icon}</div>
                <h2 className={`text-4xl font-extrabold text-gray-900 mb-8 border-l-8 ${section.accentColor} pl-6`}>
                  {section.title}
                </h2>
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                  {section.items.map((item, itemIdx) => (
                    <div key={itemIdx} className="flex items-center gap-3 bg-white p-4 rounded-xl shadow-sm border border-gray-100">
                      <div className="w-2 h-2 rounded-full bg-gradient-main" />
                      <span className="text-gray-700 font-medium">{item}</span>
                    </div>
                  ))}
                </div>
              </div>
              <div className="flex-1 w-full">
                <img 
                  src={`https://picsum.photos/seed/${section.title}/800/600`} 
                  alt={section.title}
                  className="rounded-3xl shadow-2xl border-4 border-white"
                />
              </div>
            </div>
          </div>
        </section>
      ))}
    </div>
  );
};

export default Services;

import React, { useEffect } from 'react';
import { HashRouter as Router, Routes, Route, useLocation } from 'react-router-dom';
import Header from './components/Header';
import Footer from './components/Footer';
import Home from './pages/Home';
import About from './pages/About';
import Services from './pages/Services';
import Packages from './pages/Packages';
import Contact from './pages/Contact';

// Scroll to top on navigation
const ScrollToTop = () => {
  const { pathname } = useLocation();
  useEffect(() => {
    window.scrollTo(0, 0);
  }, [pathname]);
  return null;
};

const App: React.FC = () => {
  return (
    <Router>
      <ScrollToTop />
      <div className="flex flex-col min-h-screen">
        <Header />
        <main className="flex-grow">
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/about" element={<About />} />
            <Route path="/services" element={<Services />} />
            <Route path="/packages" element={<Packages />} />
            <Route path="/contact" element={<Contact />} />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
};

export default App;

